"""
           0 -> 01
           1 -> 10
           0
           01
           0110
           01101001
    1. 
"""
import math
class Solution:
    def kthGrammar(self, n: int, k: int) -> int:
        flipAmount = 0
        rootVal = False
        def kth(n,k):
            nonlocal flipAmount
            if n == 1 or k == 1:
                return
            if k %2 == 0:
                flipAmount+=1
            kth(n-1,math.ceil(k/2))
        kth(n,k)
        rootVal = not rootVal if flipAmount % 2 == 1 else rootVal
        return int(rootVal)



s = Solution()
print(s.kthGrammar(3,1)) # must be 0
print(s.kthGrammar(2,2)) # must be 1
print(s.kthGrammar(2,1)) # must be 0 
